def createDataset(buildup, level):
    dataPoints = []
    
    # define base of signal
    for i in range(80):
        dataPoints.append(dataPoints_GOOD[i])
    
    # Calculate data depending on build up
    for i in range(81, 200):
        dataPoints.append(dataPoints_GOOD[i] + (dataPoints_BAD[i] - dataPoints_GOOD[i]) * (buildup / 100))
        
    # Calculate data depending on level <- flat line    
    #for i in range(-level + 120):
    for i in range(int(-3.8 * level + 400)):
        dataPoints.append(1)
        
    # Continue calculate data depending on build up
    for i in range(220, 260):
        dataPoints.append(dataPoints_GOOD[i] + (dataPoints_BAD[i] - dataPoints_GOOD[i]) * (buildup / 100))
    
    while len(dataPoints) < 700:
        dataPoints.append(1)
        
    return dataPoints
